import React from 'react';
import LetterSquare from './LetterSquare';
import "./letters.css";

function LetterRow({word, answer, isCurrentRow}) {

    var letters = ["", "", "", "", ""];
    for (let i = 0; i < word.length; i++) {
        letters[i] = word.charAt(i);
    }

    var squares = [];
    for (let i = 0; i < 5; i++) {
        let color = null;
        if (isCurrentRow) {
            color = "white";
        } else {
            if (answer.charAt(i) === letters[i]) {
                color = "green";
            } else if (answer.includes(letters[i])) {
                color = "yellow";
            } else {
                color = "gray";
            }
        }
        squares.push(<LetterSquare letter={letters[i]} color={color}/>);
    }

    return (
        <div class="letter-row">
            {squares}
        </div>
    )
}

export default LetterRow;
