import React from "react";
import LetterRow from "./LetterRow";
import "./letters.css";

// display 6 rows at a time
// classic version stops after 6 guesses
// two-player has unlimited rows (guesses)

function LetterGrid({pastGuesses, currentGuess, answer}) {

    var rows = [];
    pastGuesses.forEach(guess => {
        rows.push(<LetterRow guess={guess} answer={answer} isCurrentRow={false}/>);
    });
    rows.push(<LetterRow guess={currentGuess} answer={answer} isCurrentRow={true}/>)
    if (rows.length < 6) {
        for (let i = rows.length; i < 6; i++) {
            rows.push(<LetterRow guess="" answer={answer} isCurrentRow={true}/>);
        }
    }

    return (
        <div class="letter-grid">
            {rows}
        </div>
    )
}

export default LetterGrid;
