import React, { useState } from "react";

function OpponentGrid({ username, grid }) {

    // if grid exceeds 6 rows, should have scrollbar
    // grid strings: y = yellow, g = green, x = gray

    let rows = [];
    for (let str in grid) {
        let row = [];
        for (let j = 0; j < 5; j++) {
            let color = "";
            switch (str.charAt(j)) {
                case "y":
                    color = "yellow";
                    break;
                case "g":
                    color = "green";
                    break;
                case "x":
                    color = "gray";
                    break;
                default:
                    console.log("OpponentGrid color error");
            }
            row.push(
                <div className="letter-square" id={color}
                style="width: 25px; height: 25px; margin: 1px">
                </div>
            );
        }
        rows.push(<div className="letter-row">{row}</div>);
    }

    if (rows.length < 6) {
        for (let i = rows.length; i < 6; i++) {
            let row = [];
            for (let j = 0; j < 5; j++) {
                row.push(
                    <div className="letter-square" id="white"
                    style="width: 25px; height: 25px; margin: 1px">
                    </div>
                )
            }
        }
    }

    return (
        <div style="justify-content: center">
            <h6>{username}</h6>
            <div className="letter-grid">
                {rows}
            </div>
        </div>
    )
}

export default OpponentGrid;
