import React, { useEffect, useState } from "react";
import { Button, Form, FormGroup, Label, Input, Alert } from "reactstrap";
import { useNavigate } from 'react-router-dom';
import socket from "../socket";

function WorduelSetup() {

    const nav = useNavigate();

    const navWorduel = () => {
        nav("/worduel/" + code);
    }

    const [username, setUsername] = useState("");
    const [code, setCode] = useState("");
    const [alertVisible, setAlertVisible] = useState(false);

    const createNewLobby = () => {
        localStorage.setItem("username", username);
        let randomCode = Math.floor(Math.random() * (10000 - 1000) + 1000).toString();
        socket.emit("try-create-lobby", randomCode, username);
        setCode(randomCode);
    }

    const joinLobby = () => {
        localStorage.setItem("username", username);
        socket.emit("try-join-lobby", code, username);
    }

    const handleCodeDNE = () => {
        setAlertVisible(true);
    }

    useEffect(() => {
        socket.on("lobby-created", navWorduel);
        socket.on("lobby-already-exists", createNewLobby);
        socket.on("lobby-joined", navWorduel);
        socket.on("lobby-doesnt-exist", handleCodeDNE);
        return () => {
            socket.off("lobby-created", navWorduel);
            socket.off("lobby-already-exists", createNewLobby);
            socket.off("lobby-joined", navWorduel);
            socket.off("lobby-doesnt-exist", handleCodeDNE);
        }
    }, [socket])

    return (
        <div>
            <h1>
                Worduel!
            </h1>
            <Form>
                <FormGroup>
                    <Label>
                        Enter username:
                    </Label>
                    <Input 
                        value={username}
                        onChange={e => setUsername(e.target.value)}
                        name="username"
                        type="text"
                    />
                </FormGroup>
                <Button onClick={createNewLobby}>
                    Create New Lobby
                </Button>
                <h3>
                    OR
                </h3>
                <FormGroup>
                    <Label>
                        Enter code:
                    </Label>
                    <Input 
                        value={code}
                        onChange={e => setCode(e.target.value)}
                        name="code"
                        type="text"
                    />
                </FormGroup>
            </Form>
            <Button onClick={joinLobby}>
                Join Lobby
            </Button>
            <Alert color="danger" isOpen={alertVisible}>
                Lobby not found, please try again.
            </Alert>
        </div>
    )
}

export default WorduelSetup;
