import React from "react";
import "letters.css"

function LetterSquare({letter, color}) {

    var borderColor = color;
    var textColor = "white";
    if (color === "white") {
        borderColor = "#787c7e";
        textColor = "black";
    }

    return (
        <div class="letter-square"
        border-color={borderColor}
        background-color={color}
        >
            <div class="letter"
            color={textColor}
            >
                {letter}
            </div>
        </div>
    )
}

export default LetterSquare;
